import React from "react"
import PropTypes from "prop-types"
import BoxOwner from "./BoxOwner"
import './css/index.css'

import axios from 'axios';

function BoxOwnerList(props) {

  const deleteBoxOwner = (id) => {

    axios.delete('/box_owners/{id}' )
    .then(res => {
      if (res.data.success) {
        window.location.href = '/box_owners'
      } else {
        // TODO: Handle error and display message to the user
      }
    })
  }

  return (
    <React.Fragment>
      <div>
        Total BoxOwners: {props.total}
      </div>

      {props.box_owners && (
        props.box_owners.map((box_owner) => (
          <div key={`info-${box_owner.id}`} className='box'>
            <BoxOwner key={box_owner.id} first_name={box_owner.first_name} last_name={box_owner.last_name} />

            <div key={`id-${box_owner.id}`}>
              ID: {box_owner.id}
            </div>

            <div className='deleteButton'>
              <button key={`delete-${box_owner.id}`} onClick={() => deleteBoxOwner(box_owner.id)}>Delete</button>
            </div>
          </div>
        ))
      )}
    </React.Fragment>
  );
}

BoxOwnerList.propTypes = {
  box_owners: PropTypes.array,
  total: PropTypes.number
};
export default BoxOwnerList
