import React, { useState } from "react"
import './css/index.css'

import SmallBox from './images/box-small.png'
import MediumBox from './images/box-medium.png'
import LargeBox from './images/box-large.png'

function Box(props) {
  const [size, setSize] = useState(props.size);
  const [label, setLabel] = useState(props.label);

  const boxImage = (size) => {

    switch (size) {
      case 'small':
        return SmallBox
      case 'medium':
        return MediumBox
      case 'large':
        return LargeBox
    }
  }

  return (
    <React.Fragment>
        <h4 className='label'>Label: {label}</h4>
        <h3>Size: {size}</h3>
      <img src={boxImage(size)} />

      <div>
        <div>
          Attach Box Owner:
        </div>
        <div>
          <select value={box_owner} onChange={handleBoxOwnerChange}>
            <option value="no_selection"></option>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
          {errorSize !== '' && (
            <div key='errorSize' className='error'>{errorSize}</div>
          )}
        </div>
      </div>

    </React.Fragment>
  );
}

export default Box
