import React, { useState } from "react"

/* Component to validate fields in form  */
import { Formik } from "formik";

import PropTypes from "prop-types"

import axios from 'axios';

import './css/index.css'

function BoxOwnerForm(props) {
  const [first_name, setFirstName] = useState('');
  const [last_name, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [errorEmail, setErrorEmail] = useState('')
  const [errorFirstName, setErrorFirstName] = useState('')
  const [errorLastName, setErrorLastName] = useState('')

  const saveBoxOwner = () => {

    /*To check values of first_name to set error if it isn't valid*/
    if (!first_name) {
      setErrorFirstName("The First Name is required!");
    } else {
      setErrorFirstName("");
    }    

    /*To check values of last_name to set error if it isn't valid*/
    if (!last_name) {
      setErrorLastName("The Last Name is required!");
    } else {
      setErrorLastName("");
    }    

    /*To check values of email to set error if it isn't valid*/
    if (!email) {
      setErrorEmail("The Email is required!");
    } else {
      setErrorEmail("");
    }    

    /*To check if the fields don't have any error to permit call post ajax method*/
    if (!(!fist_name || !last_name || !email)) {
      axios.post(`/box_owners/create`, {size: size, label: label})
      .then(res => {
        if (res.data.success) {
          window.location.href = '/box_owners'
        } else {
          setError(res.data.error)
        }
      })
    }
  }

  const boxImage = (size) => {
    switch (size) {
      case 'small':
        return SmallBox
      case 'medium':
        return MediumBox
      case 'large':
        return LargeBox
      default:
        return NoSelection
    }
  }

  const handleSizeChange = (event) => {
    setSize(event.target.value);
  }

  return (
    <React.Fragment>
      <h3>Set Your BoxOwner</h3>

      <div className='boxOwnerForm'>
        <div>
          <div>
            Step 1: Choose a size:
          </div>
          <div>
            <select value={size} onChange={handleSizeChange}>
              <option value="no_selection"></option>
              <option value="small">Small</option>
              <option value="medium">Medium</option>
              <option value="large">Large</option>
            </select>
            {errorSize !== '' && (
              <div key='errorSize' className='error'>{errorSize}</div>
            )}
          </div>
        </div>
        <div>
          <div>
            Step 2: Label this box:
          </div>
          <div>
            <input type="text" placeholder="Label" onChange={e => setLabel(e.target.value)} />
            {errorLabel !== '' && (
              <div key='errorLabel' className='error'>{errorLabel}</div>
            )}
          </div>
        </div>
        <div className='boxPreview'>
          <img src={boxImage(size)}/>

          {label !== '' && (
            <h2>Preview: {label}</h2>
          )}
        </div>
        <div>
          <button onClick={saveBoxOwner}>Save as new box</button>

          {error !== '' && (
            <div key='error' className='error'>{error}</div>
          )}
        </div>
      </div>
    </React.Fragment>
  );
}

BoxOwnerForm.propTypes = {
  first_name: PropTypes.string,
  last_name: PropTypes.string,
  email: PropTypes.string
};
export default BoxOwnerForm
