import React, { useState } from "react"
import './css/index.css'

import SmallBox from './images/box-small.png'
import MediumBox from './images/box-medium.png'
import LargeBox from './images/box-large.png'

import axios from 'axios';

function Box(props) {
  const [size, setSize] = useState(props.size);
  const [label, setLabel] = useState(props.label);
  const [boxOwner, setBoxOwner] = useState(props.box_owner_id);
  const [errorBoxOwner, setErrorBoxOwner] = useState('')

  const boxImage = (size) => {

    switch (size) {
      case 'small':
        return SmallBox
      case 'medium':
        return MediumBox
      case 'large':
        return LargeBox
    }
  }

  const handleBoxOwnerChange = (event) => {
    setBoxOwner(event.target.value);
  }

  /*Function to attach BowOwner to Box*/
  const attachBoxOwner = () => {

    /*To check box_owner is present*/
    if (!boxOwner) {
      setErrorBoxOwner("The Bow Owner is required!");
    } else {
      setErrorBoxOwner("");
      axios.post(`/attach_box_owner`, {bow_owner: boxOwner, id: props.box_id})
      .then(res => {
        if (res.data.success) {
          window.location.href = '/box'
        } else {
          setError(res.data.error)
        }
      })
    }
  }

  return (
    <React.Fragment>
        <h4 className='label'>Label: {label}</h4>
        <h3>Size: {size}</h3>
      <img src={boxImage(size)} />

      <div>
        <div>
          Attach Box Owner:
        </div>
        <div>
          <select value={boxOwner} onChange={handleBoxOwnerChange}>
            <option value="no_selection"></option>
            {props.box_owners && (
              props.box_owners.map((box_owner) => (
                <option key={`id-${box_owner.id}`} value="{box_owner.id}">{box_owner.email}</option>
              ))
            )}
          </select>
          <div>
            <button onClick={attachBoxOwner}>Atthach box owner</button>
          </div>
          {errorBoxOwner !== '' && (
            <div key='errorBoxOwner' className='error'>{errorBoxOwner}</div>
          )}
        </div>
      </div>

    </React.Fragment>
  );
}

export default Box
